mapper.delete:

DELETE 

FROM
		CERT
WHERE 
			CERT_CODE = #{certCode}
		and CERT_EMAIL = #{certEmail}

mapper.update:
UPDATE MEMBER
SET MEM_EMAIL = CASE WHEN #{memEmail} IS NULL THEN MEM_EMAIL ELSE #{memEmail} END ,MEM_PASSWORD = CASE WHEN #{memPassword} IS NULL THEN MEM_PASSWORD ELSE #{memPassword} END ,MEM_GENDER = CASE WHEN #{memGender} IS NULL THEN MEM_GENDER ELSE #{memGender} END ,AREA_ID = CASE WHEN #{areaId} = 0 THEN AREA_ID ELSE #{areaId} END ,MEM_UPDATE = SYSDATE ,MEM_CONTENT = CASE WHEN #{memContent} IS NULL THEN MEM_CONTENT ELSE #{memContent} END ,MEM_PHOTO = CASE WHEN #{memPhoto} IS NULL THEN MEM_PHOTO ELSE #{memPhoto} END ,MEM_LEVEL = CASE WHEN #{memLevel} = 0 THEN MEM_LEVEL ELSE #{memLevel} END ,MEM_STATUS = CASE WHEN #{memStatus} = 0 THEN MEM_STATUS ELSE #{memStatus} END ,MEM_INT1 = CASE WHEN #{memInt1} = 0 THEN MEM_INT1 ELSE #{memInt1} END ,MEM_INT2 = CASE WHEN #{memInt2} = 0 THEN MEM_INT2 ELSE #{memInt2} END ,MEM_INT3 = CASE WHEN #{memInt3} = 0 THEN MEM_INT3 ELSE #{memInt3} END
MEM_ID = #{memId}
MEM_EMAIL = #{memEmail}
