mapper.insert:

INSERT INTO MEMBER(mem_id, mem_email, mem_password, mem_nickname, mem_birthday, mem_gender, area_id, mem_regdate, mem_update, mem_lastlogin, mem_content, mem_level, mem_status, mem_int1, mem_int2, mem_int3, mem_photo)
VALUES(
	MEMBER_SEQ.NEXTVAL 
	,#{memEmail}
	,#{memPassword}
	,#{memNickname}
	,to_date(#{memBirthday}, 'YYMMDD')
	,#{memGender}	
	,#{areaId}
	,SYSDATE
	,SYSDATE
	,SYSDATE
	,#{memContent}
	,#{memLevel}
	,#{memStatus}
	,#{memInt1}
	,#{memInt2}
	,#{memInt3}
	,#{memPhoto}
)

mapper.update:
UPDATE MEMBER
SET MEM_EMAIL = CASE WHEN #{memEmail} IS NULL THEN MEM_EMAIL ELSE #{memEmail} END ,MEM_PASSWORD = CASE WHEN #{memPassword} IS NULL THEN MEM_PASSWORD ELSE #{memPassword} END ,MEM_BIRTHDAY = CASE WHEN #{memBirthday} IS NULL THEN MEM_BIRTHDAY ELSE #{memBirthday} END ,MEM_NICKNAME = CASE WHEN #{memNickname} IS NULL THEN MEM_NICKNAME ELSE #{memNickname} END ,MEM_GENDER = CASE WHEN #{memGender} IS NULL THEN MEM_GENDER ELSE #{memGender} END ,AREA_ID = CASE WHEN #{areaId} = 0 THEN AREA_ID ELSE #{areaId} END ,MEM_UPDATE = SYSDATE ,MEM_CONTENT = CASE WHEN #{memContent} IS NULL THEN MEM_CONTENT ELSE #{memContent} END ,MEM_PHOTO = CASE WHEN #{memPhoto} IS NULL THEN MEM_PHOTO ELSE #{memPhoto} END
,MEM_INT1 = CASE WHEN #{memInt1} = 0 THEN MEM_INT1 ELSE #{memInt1} END ,MEM_INT2 = CASE WHEN #{memInt2} = 0 THEN MEM_INT2 ELSE #{memInt2} END ,MEM_INT3 = CASE WHEN #{memInt3} = 0 THEN MEM_INT3 ELSE #{memInt3} END
MEM_ID = #{memId}
MEM_EMAIL = #{memEmail}
