mapper.update:
UPDATE member SET mem_nickname = #{memNickname}, area_id = #{areaId}, mem_content = #{memContent},
mem_photo = #{memPhoto},
mem_update = sysdate, mem_level = #{memLevel}, mem_status = #{memStatus}, mem_int1 = #{memInt1}, mem_int2 = #{memInt2}, mem_int3 = #{memInt3} WHERE mem_id = #{memId}

mapper.select:
SELECT 	area_id 
FROM 	area 
WHERE 	area_region_key = #{ areaRegionKey }
