mapper.select:
		SELECT  team_id
       	FROM 	team
		WHERE 	team_name = #{teamName}

mapper.update:
UPDATE team SET int_id = #{intId}, team_name = #{teamName}, team_short_content = #{teamShortContent}, team_content = #{teamContent}, area_id = #{areaId},
team_photo = #{teamPhoto},
team_max = #{teamMax}
WHERE team_id = #{teamId}

mapper.select:
SELECT  int_name
FROM    interest
WHERE   int_id = #{intId}

SELECT  area_region_name
FROM    area
WHERE   area_id = #{areaId}

SELECT	mem_nickname
FROM 	member
WHERE 	mem_id = #{memId}

select * from team
where team_id in (
	select team_id from team_member where mem_id = #{ memId }
)
